curl --location 'https://origin-hsx-rust-graphql-gateway.sgp.hotstar-prod.com/graphql' \
--header 'content-type: application/json' \
--header 'x-hs-platform: android' \
--header 'x-country-code: in' \
--data-raw '{"query":"query fetchContentDetail(\n  $contentId: String!\n  $token: String!\n  $country: String!\n  $platform: String!\n  $skipCddsQuery: Boolean! = false\n  $clientCapabilities: String\n) {\n\n  fetchContentDetail(\n    hsRequest: {\n      countryCode: $country\n      platformCode: $platform\n      token: $token\n    }\n    contentId: $contentId\n    isDetail: false\n    allowWatchlistInfo: true\n    clientCapabilities: $clientCapabilities\n  ) @skip(if: $skipCddsQuery) {\n    watchProgress {\n      contentId\n      resumeAt\n      watchRatio\n      watchState\n      watchTag\n      lastUpdated\n      duration\n      watchContentMeta {\n        seasonNumber\n        episodeNumber\n      }\n    }\n\n    contentMeta {\n      ... on CollectionItem {\n        id\n        channelName\n        showID\n        parentInfo {\n          contentId\n          isPaid\n          title\n        }\n        year\n        showSeasonCount\n        duration\n        isLive\n        seasonName\n        displayTag\n      }\n      ... on ShowMeta {\n        id\n        __typename\n        coreAttributes {\n          ... CommonCoreAttributes\n        }\n        isArchived\n        isBTV\n        channelName\n        # ShowType in episode and show has different type hence here must be aliased\n        # As ShowType already used in episode before, we have to rename the showType in show\n        showShowType: showType\n        enrichedMeta {\n          metaType\n          metaValues\n        }\n      }\n      ... on MovieMeta {\n        id\n        __typename\n        coreAttributes {\n          ... CommonCoreAttributes\n        }\n        crispRatingInfo\n        subtitleLanguages {\n          name\n          displayName\n          code\n        }\n        enrichedMeta {\n          metaType\n          metaValues\n        }\n        playMeta {\n          languageSelector\n          audioFeatures {\n            code\n            label\n          }\n          duration\n          videoFeatures {\n            dynamicRanges {\n              code\n              label\n            }\n            resolution {\n              code\n              label\n            }\n          }\n          encrypted\n        }\n      }\n      ... on EpisodeMeta {\n        id\n        coreAttributes {\n          ... CommonCoreAttributes\n        }\n        showId\n        crispRatingInfo\n        seasonNumber\n        seasonId\n        episodeNumber\n        showTitle\n        seasonEpisode\n        broadcastDate\n        paywallTS\n        showType\n        channelName\n        isBTV\n        enrichedMeta {\n          metaValues\n          metaType\n        }\n        subtitleLanguages {\n          name\n          displayName\n          code\n        }\n\n        playMeta {\n          languageSelector\n          contentStartPointSeconds\n          audioFeatures {\n            code\n            label\n          }\n          duration\n          videoFeatures {\n            dynamicRanges {\n              code\n              label\n            }\n            resolution {\n              code\n              label\n            }\n          }\n          encrypted\n        }\n        showImages {\n          imageMasthead {\n            url\n          }\n          verticalImage {\n            url\n          }\n          horizontalImage {\n            url\n            height\n            width\n          }\n          titleImage {\n            url\n          }\n        }\n      }\n      ... on ClipMeta {\n        id\n        crispRatingInfo\n        playMeta {\n          languageSelector\n          audioFeatures {\n            code\n            label\n          }\n          duration\n          videoFeatures {\n            dynamicRanges {\n              code\n              label\n            }\n            resolution {\n              code\n              label\n            }\n          }\n          encrypted\n        }\n        subtitleLanguages {\n          name\n          displayName\n          code\n        }\n      }\n\n      ... on SportsMeta {\n        id\n        broadcastDate\n        sportsInfo {\n          isHighlight\n          isLive\n          isReplay\n          matchID\n          sportsType\n          tournamentSeasonId\n          tournamentSeasonName\n          sportsTournamentId\n        }\n        playMeta {\n          languageSelector\n          audioFeatures {\n            code\n            label\n          }\n          duration\n          videoFeatures {\n            dynamicRanges {\n              code\n              label\n            }\n            resolution {\n              code\n              label\n            }\n          }\n          encrypted\n        }\n        subtitleLanguages {\n          code\n          displayName\n          name\n        }\n      }\n\n      coreAttributes {\n        contentId\n        ageRating\n        contentType\n        description\n        title\n        engTitle\n        studioName\n        isCreatorContent\n        languages {\n          code\n          displayName\n        }\n        originalLanguage {\n          code\n          displayName\n        }\n        entityType\n        clipType\n        genres\n        isPaid\n        isLive\n        lifeCycleStage\n        contentProvider\n        images {\n          horizontalImage{\n            url\n            height\n            width\n          }\n          verticalImage {\n            url\n            height\n            width\n          }\n          backgroundImage {\n            url\n          }\n          imageMasthead {\n            url\n          }\n          titleImage {\n            url\n          }\n        }\n        isMonetisable\n        studioId\n        videoMetaTimelines {\n          endTimeMs\n          startTimeMs\n          elementType\n        }\n        trailers\n        startDate\n        autoplayContentId\n        isJcContent\n        isDownloadable\n      }\n    }\n    contentRating {\n      ratingList {\n        key\n      }\n      selectedRating {\n        key\n      }\n    }\n    watchListStatus\n  }\n\n}\n\nfragment CommonCoreAttributes on CoreAttributes {\n  title\n  engTitle\n  ageRating\n  description\n  isPaid\n  isLive\n  lifeCycleStage\n  studioId\n  studioName\n  isCreatorContent\n  trailers\n  languages {\n    code\n    displayName\n    name\n  }\n  genres\n  images {\n    horizontalImage {\n      url\n      theme\n    }\n    titleImage {\n      url\n      width\n      height\n    }\n    verticalImage {\n      url\n      theme\n    }\n    backgroundImage {\n      url\n    }\n    imageMasthead {\n      url\n    }\n    imageExtendedMeta {\n      imageSelectionAttributes {\n        variant\n        imageSelectionLogic\n      }\n      imageLanguages {\n        scriptLanguages\n        calloutLanguages\n      }\n    }\n  }\n  entityType\n  contentProvider\n  originalLanguage {\n    code\n  }\n  contentId\n  contentType\n  clipType\n  releaseDate\n  autoplayContentId\n  isEligibleForFreePreview\n  calloutText\n  calloutSubtext\n  releaseDateDisplayMode\n  calloutTags {\n    name\n    category\n    metaSets {\n      metaType\n      elements {\n        __typename\n        ... on LocalisableTextObject {\n          stringId\n          stringText\n          placeholderMap {\n            key\n            value\n          }\n        }\n        ... on ImageMeta {\n          url\n          height\n          width\n          alt\n        }\n      }\n    }\n  }\n  reasonToWatchTags {\n    name\n    category\n    metaSets {\n      metaType\n      elements {\n        __typename\n        ... on LocalisableTextObject {\n          stringId\n          stringText\n          placeholderMap {\n            key\n            value\n            stringId\n          }\n        }\n        ... on ImageMeta {\n          url\n          height\n          width\n          alt\n        }\n      }\n    }\n  }\n}\n\n# Write your query or mutation here","variables":'"$param"'}'
